<?require_once(OKI."/dr/drAPI.interface.php");// public static final String NO_MORE_ITERATOR_ELEMENTS = "Iterator has no more elements "define("NO_MORE_ITERATOR_ELEMENTS","Iterator has no more elements ");class HarmoniAssetIterator	extends DigitalRepositoryIterator{ // begin AssetIterator	/**	 * @var array $_assets The stored assets.	 * @access private	 */	var $_assets = array();	 	/**	 * @var int $_i The current posititon.	 * @access private	 */	var $_i = -1;		/**	 * Constructor	 */	function HarmoniAssetIterator (& $assetArray) {		// make sure that we get an array of DigitalRepository objects		ArgumentValidator::validate($assetArray, new ArrayValidatorRuleWithRule(new ExtendsValidatorRule("Asset")));				// load the types into our private array		foreach ($assetArray as $key => $val) {			$this->_assets[] =& $assetArray[$key];		}	}	// public boolean hasNext();	function hasNext() {		return ($this->_i < count($this->_assets)-1);	}	// public Type & next();	function & next() {		if ($this->hasNext()) {			$this->_i++;			return $this->_assets[$this->_i];		} else {			throwError(new Error(NO_MORE_ITERATOR_ELEMENTS, "AssetIterator", 1));		}	}} // end AssetIterator?>