<?require_once(OKI."/dr/drAPI.interface.php");// public static final String NO_MORE_ITERATOR_ELEMENTS = "Iterator has no more elements "define("NO_MORE_ITERATOR_ELEMENTS","Iterator has no more elements ");class HarmoniDigitalRepositoryIterator	extends DigitalRepositoryIterator{ // begin DigitalRepositoryIterator	/**	 * @var array $_drs The stored drs.	 * @access private	 */	var $_drs = array();	 	/**	 * @var int $_i The current posititon.	 * @access private	 */	var $_i = 0;		/**	 * Constructor	 */	function HarmoniDigitalRepositoryIterator (& $drArray) {		// make sure that we get an array of DigitalRepository objects		ArgumentValidator::validate($drArray, ArrayValidatorRuleWithRule(new ExtendsValidatorRule("DigitalRepository")));				// load the types into our private array		foreach ($drArray as $key => $val) {			$this->_drs[] =& $drArray[$key];		}	}	// public boolean hasNext();	function hasNext() {		return ($this->_i < count($this->_drs));	}	// public Type & next();	function & next() {		if ($this->hasNext()) {			$this->_i++;			return $this->_drs[$i];		} else {			throwError(new Error(NO_MORE_ITERATOR_ELEMENTS, "DigitalRepositoryIterator", 1));		}	}} // end DigitalRepositoryIterator?>