<?require_once(HARMONI_BASE."oki/shared/sharedAPI.interface.php");class HarmoniType	extends Type{ // begin Type	// public boolean isEqual(Type & $type2);	function isEqual(& $type2) {		// the domain, authority, and keyword need to be alike for the types to be equivallent		if (strtolower($this->getDomain()) == strtolower($type2->getDomain()) && 			strtolower($this->getAuthority()) == strtolower($type2->getAuthority()) &&			strtolower($this->getKeyword()) == strtolower($type2->getKeyword())) {			return TRUE;		}		// otherwise, return FALSE		return FALSE;			}	// public String getDomain();	function getDomain() {		return $this->_domain;	}	// public String getAuthority();	function getAuthority() {		return $this->_authority;	}	// public String getKeyword();	function getKeyword() {		return $this->_keyword;	}	// public String getDescription();	function getDescription() {		return $this->_description;	}} // end Type?>