<?/** * A class for passing an arbitrary input array as an iterator. * * @package harmoni.osid.shared *  * @copyright Copyright &copy; 2005, Middlebury College * @license http://www.gnu.org/copyleft/gpl.html GNU General Public License (GPL) * * @version $Id: HarmoniIterator.class.php,v 1.3 2005/01/19 17:39:39 adamfranco Exp $ */class HarmoniIterator{	/**	 * @var array $_elements The stored elements.	 * @access private	 */	var $_elements = array();	 	/**	 * @var int $_i The current posititon.	 * @access private	 */	var $_i = -1;		/**	 * Constructor	 */	function HarmoniIterator (& $elementArray) {		// load the elements into our private array		foreach (array_keys($elementArray) as $i => $key) {			$this->_elements[] =& $elementArray[$key];		}	}	// public boolean hasNext();	function hasNext() {		return ($this->_i < count($this->_elements)-1);	}	// public Type & next();	function &next() {		if ($this->hasNext()) {			$this->_i++;			return $this->_elements[$this->_i];		} else {			throwError(new Error(SharedException::NO_MORE_ITERATOR_ELEMENTS(), get_class($this), 1));		}	}}?>